import classes from '../Components/Form.module.css';
import Modal from '../Components/Modal';
import {Link, Form, redirect, useParams} from 'react-router-dom';
import { useEffect, useState } from 'react';

function NewPost(){

    const params = useParams();
    const[dataToSet,setData] = useState(null);

    useEffect(() => {
        async function fetchDataForEditin () {
            const response = await fetch('http://localhost:8080/posts/' + params.postId)
        
            const resData = await response.json();
            setData({'body': resData.post.body,'title':resData.post.author});
        };
        fetchDataForEditin();
    },[params.postId]);
    console.log(dataToSet); 


    return (
        <Modal>
        <Form method='post' className={classes.form} >
            <p >
                <label htmlFor="body">Text</label>
                <textarea id="body"  required rows={3} name = "body" value={dataToSet["body"]} ></textarea>
            </p>
            <p >
                <label htmlFor="name" >Your name</label>
                <input type="text" id="name" required name="author" value={dataToSet["title"]}/>
            </p>
            <p className={classes.actions}>
            <Link to=".." className={classes.actions} >Cancel</Link>
            <button >Submit</button>
            </p>
     
        </Form>
        </Modal>
    )
}
export default NewPost;

export async function action({request}){
    const formData = await request.formData();
    const postData = Object.fromEntries(formData);
    await fetch('http://localhost:8080/posts',{
        method:'POST',
        body:JSON.stringify(postData),
        headers:{
            'content-Type':'application/json'
        },
    });

    return redirect('/');
}

