import classes from '../Components/Form.module.css';
import Modal from '../Components/Modal';
import {Link, Form, redirect} from 'react-router-dom';


function NewPost(){

    return (
        <Modal>
        <Form method='post' className={classes.form} >
            <p >
                <label htmlFor="body">Text</label>
                <textarea id="body"  required rows={3} name = "body" value={params.content}></textarea>
            </p>
            <p >
                <label htmlFor="name" >Your name</label>
                <input type="text" id="name" required name="author" value={params.title}/>
            </p>
            <p className={classes.actions}>
            <Link to=".." className={classes.actions} >Cancel</Link>
            <button >Submit</button>
            </p>
     
        </Form>
        </Modal>
    )
}
export default NewPost;

export async function action({request}){
    const formData = await request.formData();
    const postData = Object.fromEntries(formData);
    await fetch('http://localhost:8080/posts',{
        method:'POST',
        body:JSON.stringify(postData),
        headers:{
            'content-Type':'application/json'
        },
    });

    return redirect('/');
}

export async function editAction ({params}) {
    const postId = params['post-id']; // Fetch the postId from the URL params
      // Fetch post data based on postId (You may have your own API or method)
      const response = await fetch(`/api/posts/${postId}`); // Replace with your API endpoint
      const postData = await response.json();
  
      // Return the populated data to be used in the PostForm component
      return {
        initialValues: {
          title: postData.title,
          content: postData.content,
          // Other fields you want to pre-fill in the form for editing
        }
      };
  };
