import classes from '../Components/Form.module.css';
import Modal from '../Components/Modal';
import {Link, Form, redirect, useParams} from 'react-router-dom';
import { useEffect, useState } from 'react';

function NewPost(){

    const params = useParams();
    
    const[dataToSet,setData] = useState([null,null]);
    useEffect(() => {
        async function fetchDataForEditin () {
            if(params.postId){
            const response = await fetch('http://localhost:8080/posts/' + params.postId)
        
            const resData = await response.json();
            setData( [resData.post.body,resData.post.author]);
            } 
        };
        fetchDataForEditin();
    },[params.postId]);
    
    // console.log(dataToSet); 
    // Call the update function passing the postId
    const handleUpdate = async () => {
        try {
        await update({ postId: params.postId }); // Pass the postId to the update function
        } catch (error) {
        console.error('Error updating post:', error);
        }
    };

    return (
        <Modal>
        <Form method='post' className={classes.form} >
            <p >
                <label htmlFor="body">Text</label>
                <textarea id="body"  required rows={3} name = "body" defaultValue={dataToSet[0]} ></textarea>
            </p>
            <p >
                <label htmlFor="name" >Your name</label>
                <input type="text" id="name" required name="author" defaultValue={dataToSet[1]}/>
            </p>
            <p className={classes.actions}>
            <Link to=".." className={classes.actions} >Cancel</Link>
            <button onClick={handleUpdate}>Submit</button>
            </p>
     
        </Form>
        </Modal>
    )
}
export default NewPost;

export async function action({request}){
    const formData = await request.formData();
    const postData = Object.fromEntries(formData);
    await fetch('http://localhost:8080/posts',{
        method:'POST',
        body:JSON.stringify(postData),
        headers:{
            'content-Type':'application/json'
        },
    });

    return redirect('/');
}

export async function update({postId},{request}){
    // const {id}= useParams();
    const formData = await request.formData();
    const postData = Object.fromEntries(formData);
    console.log(postId)
    await fetch('http://localhost:8080/posts/&{postId}',{
        method:'POST',
        body:JSON.stringify(postData),
        headers:{
            'content-Type':'application/json'
        },
    });

    return redirect('/');
}
